// Initialize variables
const chunks = [];
const chunkSize = 2000;
const chunkOverlap = 0;
// Fix: Use proper string replacement for all newlines and handle undefined
const text = $("Extract from File").item.json.text.replace(/\n/g, '');

// Fix: Correct chunk calculation and loop condition
for (let i = 0; i < text.length; i += chunkSize - chunkOverlap) {
    const chunk = text.slice(
        i,  // Start index
        i + chunkSize  // End index
    );

    // Only add non-empty chunks
    if (chunk.length > 0) {
        chunks.push(chunk);
    }
}

return { chunks };

